INSERT INTO `service` (`id`, `title`, `description`, `short_description`, `image`, `list`, `title_eng`, `description_eng`, `list_eng`, `short_description_eng`, `created_at`, `updated_at`) VALUES
(6, 'Engineering', '<h4><strong>Conceptual - Feasibility Study</strong></h4><p>Studi konseptual dan kelayakan proyek untuk menentukan viabilitas teknis dan ekonomis dari suatu proyek engineering.</p><p>&nbsp;</p><h4><strong>Pre Front End Engineering Design (Pre Feed)</strong></h4><p>Tahap awal desain engineering untuk mengidentifikasi konsep dan alternatif solusi sebelum FEED.</p><p>&nbsp;</p><h4><strong>Front End Engineering Design (FEED)</strong></h4><p>Desain engineering tahap awal yang komprehensif untuk mendefinisikan lingkup, biaya, dan jadwal proyek.</p><p>&nbsp;</p><h4><strong>Detail Engineering Design (DED)</strong></h4><p>Desain engineering detail yang lengkap dan siap untuk konstruksi dengan spesifikasi teknis yang komprehensif.</p><p>&nbsp;</p><h4><strong>Hazard and Operability (HAZOP) Study</strong></h4><p>Studi analisis bahaya dan operabilitas sistematis untuk mengidentifikasi potensi risiko dalam proses operasional.</p><p>&nbsp;</p><h4><strong>Hazard Identification (HAZID) Study</strong></h4><p>Studi identifikasi bahaya untuk mengenali dan mengevaluasi potensi risiko dalam tahap awal proyek.</p><p>&nbsp;</p><h4><strong>Design Review</strong></h4><p>Review dan evaluasi desain engineering untuk memastikan kualitas, keamanan, dan kepatuhan terhadap standar.</p><p>&nbsp;</p><h4><strong>Flow Assurance Study</strong></h4><p>Studi jaminan aliran untuk memastikan transportasi fluida yang optimal dalam sistem perpipaan dan proses.</p><p>&nbsp;</p><p>&nbsp;</p>', '<p>SENA memiliki keahlian menyusun dokumen engineering design untuk semua tahapan engineering yang dibutuhkan. Layanan yang dapat SENA lakukan di bidang engineering meliputi berbagai tahapan mulai dari studi konseptual hingga desain detail yang siap konstruksi.</p>', 'one1729993260.jpg', '', 'Engineering', '<h4><strong>Conceptual - Feasibility Study</strong></h4><p>Studi konseptual dan kelayakan proyek untuk menentukan viabilitas teknis dan ekonomis dari suatu proyek engineering.</p><p>&nbsp;</p><h4><strong>Pre Front End Engineering Design (Pre Feed)</strong></h4><p>Tahap awal desain engineering untuk mengidentifikasi konsep dan alternatif solusi sebelum FEED.</p><p>&nbsp;</p><h4><strong>Front End Engineering Design (FEED)</strong></h4><p>Desain engineering tahap awal yang komprehensif untuk mendefinisikan lingkup, biaya, dan jadwal proyek.</p><p>&nbsp;</p><h4><strong>Detail Engineering Design (DED)</strong></h4><p>Desain engineering detail yang lengkap dan siap untuk konstruksi dengan spesifikasi teknis yang komprehensif.</p><p>&nbsp;</p><h4><strong>Hazard and Operability (HAZOP) Study</strong></h4><p>Studi analisis bahaya dan operabilitas sistematis untuk mengidentifikasi potensi risiko dalam proses operasional.</p><p>&nbsp;</p><h4><strong>Hazard Identification (HAZID) Study</strong></h4><p>Studi identifikasi bahaya untuk mengenali dan mengevaluasi potensi risiko dalam tahap awal proyek.</p><p>&nbsp;</p><h4><strong>Design Review</strong></h4><p>Review dan evaluasi desain engineering untuk memastikan kualitas, keamanan, dan kepatuhan terhadap standar.</p><p>&nbsp;</p><h4><strong>Flow Assurance Study</strong></h4><p>Studi jaminan aliran untuk memastikan transportasi fluida yang optimal dalam sistem perpipaan dan proses.</p><p>&nbsp;</p><p>&nbsp;</p>', NULL, '<p>SENA memiliki keahlian menyusun dokumen engineering design untuk semua tahapan engineering yang dibutuhkan. Layanan yang dapat SENA lakukan di bidang engineering meliputi berbagai tahapan mulai dari studi konseptual hingga desain detail yang siap konstruksi.</p>', '2024-10-26 18:41:00', '2025-07-13 19:11:04'),
(7, 'Inspections', '<h2><strong>Selama Pembangunan / During Construction</strong></h2><p>&nbsp;</p><h4><strong>Welding and Coating Inspection</strong></h4><p>Inspeksi pengelasan dan coating untuk memastikan kualitas sambungan dan perlindungan korosi sesuai standar.</p><p>&nbsp;</p><h4><strong>Material Testing &amp; Inspection</strong></h4><p>Pengujian dan inspeksi material untuk memverifikasi kualitas dan kesesuaian spesifikasi material konstruksi.</p><p>&nbsp;</p><h4><strong>Non-Destructive Testing (Conventional &amp; Advance)</strong></h4><p>Pengujian tanpa merusak menggunakan teknologi konvensional dan canggih untuk deteksi cacat material.</p><p>&nbsp;</p><h4><strong>Destructive Testing</strong></h4><p>Pengujian destruktif untuk analisis mendalam karakteristik material dan verifikasi kekuatan struktur.</p><p>&nbsp;</p><p>&nbsp;</p><h2><strong>Pasca Operasi / Post Operation</strong></h2><p>&nbsp;</p><h4><strong>Pipeline and Facility Inspection &amp; Assessment</strong></h4><p>Inspeksi dan penilaian kondisi pipeline dan fasilitas untuk evaluasi integritas operasional.</p><p>&nbsp;</p><h4><strong>Intelligent Pigging</strong></h4><p>Inspeksi pipeline menggunakan teknologi intelligent pig untuk deteksi korosi dan cacat internal.</p><p>&nbsp;</p><h4><strong>Reliability Study</strong></h4><p>Studi keandalan sistem untuk mengoptimalkan performa dan meminimalkan risiko kegagalan.</p><p>&nbsp;</p><h4><strong>Risk Assessment and Mitigation</strong></h4><p>Penilaian risiko komprehensif dan strategi mitigasi untuk keamanan operasional.</p><p>&nbsp;</p><h4><strong>Risk Based Inspection</strong></h4><p>Inspeksi berbasis risiko untuk optimalisasi jadwal dan metode inspeksi berdasarkan tingkat risiko.</p><p>&nbsp;</p><h4><strong>Fitness For Services</strong></h4><p>Evaluasi kelayakan operasi fasilitas untuk menentukan kemampuan beroperasi dengan aman.</p><p>&nbsp;</p><h4><strong>Remaining Life Assessment</strong></h4><p>Penilaian sisa umur operasi untuk perencanaan maintenance dan penggantian asset.</p><p>&nbsp;</p><h4><strong>Failure Analysis</strong></h4><p>Analisis kegagalan untuk identifikasi penyebab dan pencegahan kegagalan serupa di masa depan.</p><p>&nbsp;</p><h4><strong>External Corrosion Direct Assessment (CIPS, DCVG, PCM)</strong></h4><p>Penilaian korosi eksternal langsung menggunakan metode CIPS, DCVG, dan PCM untuk evaluasi kondisi coating.</p><p>&nbsp;</p><h4><strong>Geohazard Assessment</strong></h4><p>Penilaian bahaya geologi untuk identifikasi risiko geoteknik terhadap integritas fasilitas.</p><p>&nbsp;</p><h4><strong>Jetty Assessment (Non Destructive &amp; Destructive Test)</strong></h4><p>Penilaian kondisi dermaga menggunakan pengujian destruktif dan non-destruktif untuk evaluasi struktural.</p><p>&nbsp;</p><h4><strong>Asset Integrity Management System (AIMS)</strong></h4><p>Sistem manajemen integritas asset untuk pemantauan dan pengelolaan kondisi fasilitas secara komprehensif.</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>', '<p>SENA memiliki keahlian dalam inspeksi dan pengujian teknis, integrity serta pekerjaan pasca operasi. Layanan yang dapat SENA lakukan di bidang inspeksi meliputi inspeksi selama pembangunan dan pasca operasi dengan teknologi terdepan dan standar internasional.</p>', 'one1729993328.jpg', '', 'Inspections', '<h2><strong>Selama Pembangunan / During Construction</strong></h2><p>&nbsp;</p><h4><strong>Welding and Coating Inspection</strong></h4><p>Inspeksi pengelasan dan coating untuk memastikan kualitas sambungan dan perlindungan korosi sesuai standar.</p><p>&nbsp;</p><h4><strong>Material Testing &amp; Inspection</strong></h4><p>Pengujian dan inspeksi material untuk memverifikasi kualitas dan kesesuaian spesifikasi material konstruksi.</p><p>&nbsp;</p><h4><strong>Non-Destructive Testing (Conventional &amp; Advance)</strong></h4><p>Pengujian tanpa merusak menggunakan teknologi konvensional dan canggih untuk deteksi cacat material.</p><p>&nbsp;</p><h4><strong>Destructive Testing</strong></h4><p